(978, 4, '2013-01-28 19:29:02', '2013-01-28 18:29:02', '##################################################\r\n# Abschiedswort\r\n# Python-Script bitte in den Scripteditor von Maya einsetzeneingebenmaya script editor\r\n# Script markieren und mit CTRL-Enter ausführen\r\nimport maya.cmds as cmds\r\ndef licht():\r\ncmds.pointLight(name="Licht_blau", intensity=5, rgb=(0,0,1))\r\ncmds.defaultNavigation(createNew=1, destination="Licht_blauShape.fogGeometry")\r\ncmds.setAttr("Licht_blau.fogRadius",50)\r\n\r\ndef typo():\r\ncmds.textCurves(font="Verdana", text="SERVUS")\r\ncmds.select(''Text_SERVUS_1'')\r\ncmds.scale(5,5,5)\r\ncmds.rotate(50,40,40)\r\ncmds.move(-4,-4,8)\r\ncmds.planarSrf(''Char_S_1'', name=''Buchstabe_S_1'', polygon=0, object=1)\r\ncmds.planarSrf(''Char_E_1'', name=''Buchstabe_E_1'', polygon=0, object=1)\r\ncmds.planarSrf(''Char_R_1'', name=''Buchstabe_R_1'', polygon=0, object=1)\r\ncmds.planarSrf(''Char_V_1'', name=''Buchstabe_V_1'', polygon=0, object=1)\r\ncmds.planarSrf(''Char_U_1'', name=''Buchstabe_U_1'', polygon=0, object=1)\r\ncmds.planarSrf(''Char_S_2'', name=''Buchstabe_S_2'', polygon=0, object=1)\r\n\r\ndef gluehen():\r\ncmds.setAttr("lambert1.incandescence",  1, 0.8, 0, type="double3")\r\ncmds.setAttr("lambert1.hideSource", 1)\r\ncmds.setAttr("lambert1.glowIntensity", 0.5)\r\n\r\nlicht()\r\ntypo()\r\ngluehen()\r\n\r\n# Die Szene bitte rendern\r\n##################################################', 'Prof. Jens Müller', '', 'publish', 'closed', 'closed', '', 'jens-mueller', '', '', '2013-01-30 10:32:56', '2013-01-30 09:32:56', '', 0, 'http://werkschau.hs-augsburg.de/ss_13/?post_type=betreuer&#038;p=978', 0, 'betreuer', '', 0),
